mapper.update:
update tbl_member set email = #{email}, enabled = #{enabled}, levels = #{levels}, point = #{point}, update_date = systimestamp,
user_pw = #{user_pw},
user_name = #{user_name} where user_id = #{user_id}

mapper.select:
SELECT TableB.* FROM
(
    SELECT ROWNUM AS RNUM, TableA.* FROM
    (
        SELECT * FROM tbl_member

        ORDER BY reg_date DESC
    ) TableA

    WHERE ROWNUM <= #{queryStartNo}+#{queryPerPageNum}
) TableB WHERE TableB.RNUM > #{queryStartNo}
